#include <openenclave/host.h>
#include <stdio.h>

// Include the untrusted microbench header that is generated
// during the build. This file is generated by calling the
// sdk tool oeedger8r against the microbench.edl file.
#include "microbench_u.h"

int main(int argc, const char* argv[])
{
    oe_result_t result;
    int ret = 1, iters = 1;
    oe_enclave_t* enclave = NULL;

    uint32_t flags = OE_ENCLAVE_FLAG_DEBUG;

    if (argc != 2)
    {
        fprintf(stderr, "Usage: %s enclave_image_path\n", argv[0]);
        goto exit;
    }

    result = oe_create_microbench_enclave(
        argv[1], OE_ENCLAVE_TYPE_AUTO, flags, NULL, 0, &enclave);

    if (result != OE_OK)
    {
        fprintf(
            stderr,
            "oe_create_microbench_enclave(): result=%u (%s)\n",
            result,
            oe_result_str(result));
        goto exit;
    }

    // Call into the enclave
    printf("Calling into enclave %d times\n",iters);   
   
    for (int i = 0; i < iters; i++){
   	
	//result = ecall(enclave);
	// 5 params
	//result = ecall_5(enclave,iters,iters,iters,iters,iters);    
	// 10 params
//	result = ecall_10(enclave,iters,iters,iters,iters,iters,
//			  iters,iters,iters,iters,iters);    
	// 15 params
	result = ecall_15(enclave,iters,iters,iters,iters,iters,
			  iters,iters,iters,iters,iters,
			  iters,iters,iters,iters,iters);  
	// 20 params
//	result = ecall_20(enclave,iters,iters,iters,iters,iters,
//			  iters,iters,iters,iters,iters
//			  iters,iters,iters,iters,iters
//			  iters,iters,iters,iters,iters);  
  	 if (result != OE_OK)
    	{
        	fprintf(
            		stderr,
            		"calling into enclave_microbench failed: result=%u (%s)\n",
            		result,
           		 oe_result_str(result));
        	goto exit;
    	}
    }

    ret = 0;

exit:
    // Clean up the enclave if we created one
    if (enclave)
        oe_terminate_enclave(enclave);

    return ret;
}
